SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_valor_mat_deducao ( nr_seq_material_p bigint, nr_seq_valor_mat_p bigint, nr_seq_mat_novo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_valor_w		mat_atend_paciente_valor.ie_tipo_valor%type;
ie_valor_informado_w	material_atend_paciente.ie_valor_informado%type;
vl_material_w		material_atend_paciente.vl_material%type;
nr_interno_conta_w	material_atend_paciente.nr_interno_conta%type;
nr_interno_conta_novo_w	conta_paciente.nr_interno_conta%type;
nr_prescricao_w		material_atend_paciente.nr_prescricao%type;
cd_acao_w		material_atend_paciente.cd_acao%type;
qt_registro_w		integer;


BEGIN

if (coalesce(nr_seq_material_p,0) > 0) and (coalesce(nr_seq_valor_mat_p,0) > 0) then

	select	nr_interno_conta,
		nr_prescricao,
		cd_acao
	into STRICT	nr_interno_conta_w,
		nr_prescricao_w,
		cd_acao_w
	from	material_atend_paciente
	where	nr_sequencia = nr_seq_material_p;

	select	ie_tipo_valor,
		vl_material
	into STRICT	ie_tipo_valor_w,
		vl_material_w
	from	mat_atend_paciente_valor
	where	nr_sequencia = nr_seq_valor_mat_p
	and	nr_seq_material = nr_seq_material_p;

	if (ie_tipo_valor_w = 95) then
		ie_valor_informado_w	:= 'S';
	else
		ie_valor_informado_w	:= 'N';
	end if;

	select	count(1)
	into STRICT	qt_registro_w
	from	material_atend_paciente
	where	nr_sequencia	= nr_seq_mat_novo_p;

	if (qt_registro_w > 0) then
		select	nr_interno_conta
		into STRICT	nr_interno_conta_novo_w
		from	material_atend_paciente
		where	nr_sequencia	= nr_seq_mat_novo_p;

		/*Retornar o valor do material para o valor antes da dedução*/

		update	material_atend_paciente
		set	ie_valor_informado = ie_valor_informado_w,
			vl_material = vl_material_w,
			vl_unitario = dividir(vl_material_w, qt_material)
		where	nr_sequencia = nr_seq_mat_novo_p;

		/*Atualizar o valor do material, se não informado manualmente, caso tenha alguma alteração de preço*/

		if (ie_valor_informado_w = 'N') and
			((cd_acao_w <> 2) or (coalesce(nr_prescricao_w::text, '') = '')) then
			CALL atualiza_preco_material(nr_seq_mat_novo_p, nm_usuario_p);
		else
			CALL gerar_tributo_conta_pac(nr_interno_conta_novo_w, nr_seq_mat_novo_p, 'M', nm_usuario_p);
		end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
