CREATE OR REPLACE PROCEDURE atualizar_valor_procedimento ( nr_seq_nf_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
cd_procedimento_w	bigint;
nr_item_nf_w		integer;
qt_procedimento_w	double precision;
vl_preco_w		double precision;
vl_desconto_w		double precision;
vl_total_item_nf_w	double precision;

 
c01 CURSOR FOR 
SELECT	a.nr_item_nf, 
	a.cd_procedimento, 
	qt_item_nf, 
	vl_desconto, 
	coalesce(Obter_preco_procedimento(	cd_estabelecimento_p, 
					cd_convenio_p, 
					cd_categoria_p, 
					b.dt_entrada_saida, 
					a.cd_procedimento, 
					a.ie_origem_proced, 
					0,0,0,null,null,null,null,0,0,'P'),0) 
from	nota_fiscal_item a, 
	nota_fiscal b 
where	a.nr_sequencia = nr_seq_nf_p 
and	(a.cd_procedimento IS NOT NULL AND a.cd_procedimento::text <> '') 
and	a.nr_sequencia = b.nr_sequencia;


BEGIN 
 
open c01;
loop 
fetch c01 into	 
	nr_item_nf_w, 
	cd_procedimento_w, 
	qt_procedimento_w, 
	vl_desconto_w, 
	vl_preco_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	vl_total_item_nf_w := (vl_preco_w * qt_procedimento_w);
 
	update	nota_fiscal_item 
	set	vl_unitario_item_nf = vl_total_item_nf_w / qt_procedimento_w, 
		vl_liquido = vl_total_item_nf_w - vl_desconto_w, 
		vl_total_item_nf = vl_total_item_nf_w 
	where	nr_item_nf = nr_item_nf_w 
	and	nr_sequencia = nr_seq_nf_p;
	commit;
	end;
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
