SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_valor_proc_deducao ( nr_seq_procedimento_p bigint, nr_seq_valor_proc_p bigint, nr_seq_proc_novo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_valor_w		proc_paciente_valor.ie_tipo_valor%type;
ie_valor_informado_w	procedimento_paciente.ie_valor_informado%type;
vl_procedimento_w	procedimento_paciente.vl_procedimento%type;
vl_medico_w		procedimento_paciente.vl_medico%type;
vl_anestesista_w	procedimento_paciente.vl_anestesista%type;
vl_materiais_w		procedimento_paciente.vl_materiais%type;
vl_auxiliares_w		procedimento_paciente.vl_auxiliares%type;
vl_custo_operacional_w	procedimento_paciente.vl_custo_operacional%type;
nr_interno_conta_w	procedimento_paciente.nr_interno_conta%type;
cd_convenio_w		procedimento_paciente.cd_convenio%type;
qt_registro_w		integer;
nr_interno_conta_novo_w	conta_paciente.nr_interno_conta%type;


BEGIN

if (coalesce(nr_seq_procedimento_p,0) > 0) and (coalesce(nr_seq_valor_proc_p,0) > 0) then

	select	nr_interno_conta,
		cd_convenio
	into STRICT	nr_interno_conta_w,
		cd_convenio_w
	from	procedimento_paciente
	where	nr_sequencia = nr_seq_procedimento_p;

	select	ie_tipo_valor,
		vl_procedimento,
		vl_medico,
		vl_anestesista,
		vl_materiais,
		vl_auxiliares,
		vl_custo_operacional
	into STRICT	ie_tipo_valor_w,
		vl_procedimento_w,
		vl_medico_w,
		vl_anestesista_w,
		vl_materiais_w,
		vl_auxiliares_w,
		vl_custo_operacional_w
	from	proc_paciente_valor
	where	nr_sequencia = nr_seq_valor_proc_p
	and	nr_seq_procedimento = nr_seq_procedimento_p;

	if (ie_tipo_valor_w = 95) then
		ie_valor_informado_w	:= 'S';
	else
		ie_valor_informado_w	:= 'N';
	end if;

	select	count(1)
	into STRICT	qt_registro_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_proc_novo_p;

	if (qt_registro_w > 0) then
		select	nr_interno_conta
		into STRICT	nr_interno_conta_novo_w
		from	procedimento_paciente
		where	nr_sequencia	= nr_seq_proc_novo_p;

		/*Retornar o valor do procedimento para o valor antes da dedução*/

		update	procedimento_paciente
		set	ie_valor_informado = ie_valor_informado_w,
			vl_procedimento = vl_procedimento_w,
			vl_medico = vl_medico_w,
			vl_anestesista = vl_anestesista_w,
			vl_materiais = vl_materiais_w,
			vl_auxiliares = vl_auxiliares_w,
			vl_custo_operacional = vl_custo_operacional_w
		where	nr_sequencia = nr_seq_proc_novo_p;

		/*Atualizar o valor do procedimento, se não informado manualmente, caso tenha alguma alteração de preço*/

		if (ie_valor_informado_w = 'N') then
			CALL atualiza_preco_procedimento(nr_seq_proc_novo_p, cd_convenio_w, nm_usuario_p);
		else
			CALL gerar_tributo_conta_pac(nr_interno_conta_novo_w, nr_seq_proc_novo_p, 'P', nm_usuario_p);
		end if;
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
