SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_venc_lista_prot ( dt_vencimento_p timestamp, nr_protocolos_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w		bigint;
cd_estabelecimento_w	bigint;
ie_alt_venc_tit_w	varchar(255);
nr_seq_protocolo_w	bigint;
ds_texto_w		varchar(255);

c01 CURSOR FOR 
SELECT	nr_titulo 
from	titulo_receber 
where	nr_seq_protocolo	= nr_seq_protocolo_w 

union
 
SELECT	nr_titulo 
from	titulo_receber 
where	nr_interno_conta in (select	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo	= nr_seq_protocolo_w);
	
c02 CURSOR FOR 
	SELECT	nr_seq_protocolo 
	from	protocolo_convenio 
	where	obter_se_contido(nr_seq_protocolo,nr_protocolos_p) = 'S';


BEGIN 
 
open C02;
loop 
fetch C02 into	 
	nr_seq_protocolo_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	 
	update	protocolo_convenio 
	set	dt_vencimento 	= dt_vencimento_p, 
		nm_usuario	= nm_usuario_p, 
		dt_atualizacao	= clock_timestamp()	 
	where 	nr_seq_protocolo = nr_seq_protocolo_w;
 
	select	cd_estabelecimento 
	into STRICT	cd_estabelecimento_w 
	from	protocolo_convenio 
	where 	nr_seq_protocolo = nr_seq_protocolo_w;
 
	ie_alt_venc_tit_w := obter_param_usuario(85, 142, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_alt_venc_tit_w);
	 
	if (coalesce(ie_alt_venc_tit_w, 'N') = 'S') then 
		begin 
		 
		ds_texto_w := substr(wheb_mensagem_pck.get_texto(302767),1,255);--Alteração através do protocolo 
		
		open c01;
		loop 
		fetch c01 into 
			nr_titulo_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			CALL ALTERAR_VENCIMENTO_TIT_REC(nr_titulo_w, dt_vencimento_p, 1, ds_texto_w || ' ' || nr_seq_protocolo_w, nm_usuario_p);
		end loop;
		close c01;
		 
		end;
	end if;
	 
	end;
end loop;
close C02;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
