SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_vinculo_proj_gpi_ata (ie_proc_p text, nr_seq_proj_gpi_p bigint, nr_seq_ata_p bigint) AS $body$
DECLARE

/* V - vincular
     D - desvincular */
qt_registro bigint;

BEGIN

select 	count(*)
into STRICT 	qt_registro
from 	gpi_projeto
where	nr_sequencia = nr_seq_proj_gpi_p;

if ((upper(ie_proc_p) = 'V') and (qt_registro > 0)) then
	update    proj_ata
	set       nr_seq_proj_gpi 	= nr_seq_proj_gpi_p
	where     nr_sequencia 		= nr_seq_ata_p;
else
	update    proj_ata
	set       nr_seq_proj_gpi 	 = NULL
	where     nr_sequencia 		= nr_seq_ata_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
