CREATE OR REPLACE PROCEDURE atualizar_visao_schematic ( cd_funcao_p bigint, nm_tabela_p text, nr_seq_visao_p bigint, nm_usuario_p text, ie_atualizou_p INOUT text) AS $body$
DECLARE


qt_ocorrencias_w	bigint;


BEGIN

ie_atualizou_p	:= 'N';

if (coalesce(nr_seq_visao_p,0) > 0) then

	select	count(*)
	into STRICT	qt_ocorrencias_w
	from	objeto_schematic
	where	cd_funcao				=	cd_funcao_p
	and		ie_tipo_componente		=	'WDBP'
	and		nm_tabela				=	nm_tabela_p
	and		coalesce(nr_seq_visao::text, '') = '';

	if (qt_ocorrencias_w = 1) then
			update	objeto_schematic
			set		nr_seq_visao			=	nr_seq_visao_p,
					nm_usuario				=	nm_usuario_p,
					dt_atualizacao			= 	clock_timestamp()
			where	cd_funcao				=	cd_funcao_p
			and		ie_tipo_componente		=	'WDBP'
			and		nm_tabela				=	nm_tabela_p
			and		coalesce(nr_seq_visao::text, '') = '';

			ie_atualizou_p	:= 'S';
	end if;

commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
