CREATE OR REPLACE PROCEDURE atualizar_vl_especie_deposito ( nr_seq_deposito_p bigint, vl_especie_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* Projeto Multimoeda - Variáveis */

cd_estabelecimento_w	smallint;
cd_moeda_deposito_w	integer;
cd_moeda_empresa_w	integer;
vl_cotacao_w		cotacao_moeda.vl_cotacao%type;


BEGIN

if (nr_seq_deposito_p IS NOT NULL AND nr_seq_deposito_p::text <> '') and (vl_especie_p IS NOT NULL AND vl_especie_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	/* Projeto Multimoeda - Busca os dados do depósito para verificar se é moeda estrangeira */

	select	max(cd_estabelecimento),
		max(cd_moeda),
		max(vl_cotacao)
	into STRICT	cd_estabelecimento_w,
		cd_moeda_deposito_w,
		vl_cotacao_w
	from 	deposito
	where	nr_sequencia = nr_seq_deposito_p;

	/* Projeto Multimoeda - Busca a moeda padrão da empresa.*/

	select	obter_moeda_padrao_empresa(cd_estabelecimento_w,'E')
	into STRICT	cd_moeda_empresa_w
	;

	/* Projeto Multimoeda - Verifica se o depósito é em moeda estrangeira para atualizar os valores.*/

	if (coalesce(cd_moeda_deposito_w,cd_moeda_empresa_w) <> cd_moeda_empresa_w) and (coalesce(vl_cotacao_w,0) <> 0) then
		begin
		update	deposito a
		set	a.vl_especie_estrang = coalesce(a.vl_especie_estrang,0) + (coalesce(vl_especie_p,0) / vl_cotacao),
			a.vl_complemento = (coalesce(a.vl_cheque,0) - coalesce(a.vl_cheque_estrang,0)) +
					((coalesce(a.vl_especie,0) + coalesce(vl_especie_p,0)) - (coalesce(a.vl_especie_estrang,0) + (coalesce(vl_especie_p,0) / vl_cotacao))),
			a.vl_especie   = coalesce(a.vl_especie,0) + coalesce(vl_especie_p,0)
		where	a.nr_sequencia = nr_seq_deposito_p;
		end;
	else
		begin
		update	deposito a
		set	a.vl_especie   = coalesce(a.vl_especie,0) + coalesce(vl_especie_p,0)
		where	a.nr_sequencia = nr_seq_deposito_p;
		end;
	end if;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
