CREATE OR REPLACE PROCEDURE atualizar_vl_glosa_ret_conv ( nr_seq_conv_ret_glosa_p bigint, qt_glosa_p bigint, vl_glosa_p bigint, cd_motivo_glosa_p bigint, ds_observacao_p text, vl_amaior_p bigint, ie_tipo_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_conv_ret_glosa_p IS NOT NULL AND nr_seq_conv_ret_glosa_p::text <> '') and (qt_glosa_p IS NOT NULL AND qt_glosa_p::text <> '') and (vl_glosa_p IS NOT NULL AND vl_glosa_p::text <> '') then

begin

	if (ie_tipo_p = 1) then
	begin
		update	convenio_retorno_glosa
		set	qt_glosa	= qt_glosa + qt_glosa_p,
			vl_glosa	= vl_glosa_p,
			cd_motivo_glosa	= cd_motivo_glosa_p,
			ds_observacao	= ds_observacao_p
		where	nr_sequencia	= nr_seq_conv_ret_glosa_p;
	end;

	else if (ie_tipo_p = 2) then
	begin
		update	convenio_retorno_glosa
		set	qt_glosa	= qt_glosa + qt_glosa_p,
			vl_glosa	= 0,
			vl_amaior	= vl_amaior_p,
			cd_motivo_glosa	= cd_motivo_glosa_p,
			ds_observacao	= ds_observacao_p
		where	nr_sequencia	= nr_seq_conv_ret_glosa_p;
	end;

	else
	begin
		update	convenio_retorno_glosa
		set	vl_glosa	= vl_glosa_p,
			cd_motivo_glosa	= cd_motivo_glosa_p
		where	nr_sequencia	= nr_seq_conv_ret_glosa_p;
	end;

	end if;

	end if;

	commit;

end;

end if;

end;
