CREATE OR REPLACE PROCEDURE atualizar_vl_mat_autor_cir ( cd_opcao_p bigint, vl_material_p bigint, nr_seq_mat_autor_cirurgia_p bigint, ie_origem_preco_p bigint, nm_usuario_p text) AS $body$
DECLARE

/*
opcoes
1 -  grava o valor do material, valor unitario do material e origem do preco 
2 - grava o valor do material
*/
BEGIN

if (nr_seq_mat_autor_cirurgia_p IS NOT NULL AND nr_seq_mat_autor_cirurgia_p::text <> '' AND vl_material_p IS NOT NULL AND vl_material_p::text <> '') then

	if (cd_opcao_p = 1) then
		update	material_autor_cirurgia
		set	vl_material		= coalesce(qt_solicitada,0) * vl_material_p, 
			vl_unitario_material	= vl_material_p, 
			ie_origem_preco		= ie_origem_preco_p,
            nm_usuario = nm_usuario_p,
            dt_atualizacao = clock_timestamp()
		where	nr_sequencia		= nr_seq_mat_autor_cirurgia_p;
		
	elsif (cd_opcao_p = 2) then
		update	material_autor_cirurgia
		set	vl_material	= CASE WHEN coalesce(qt_material,0)=0 THEN coalesce(qt_solicitada,0)  ELSE qt_material END  * vl_material_p,
            nm_usuario = nm_usuario_p,
            dt_atualizacao = clock_timestamp()
		where	nr_sequencia	= nr_seq_mat_autor_cirurgia_p;
	end if;
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
