CREATE OR REPLACE PROCEDURE atualizar_vl_pago_tit_pagar ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_pago_juros_w		double precision;
vl_pago_multa_w		double precision;
vl_baixa_w		double precision;
nr_sequencia_w		bigint;


BEGIN

if (coalesce(nr_titulo_p,0) <> 0) then

	select	coalesce(max(vl_baixa),0),
		coalesce(max(vl_multa),0),
		coalesce(max(vl_juros),0),
		max(nr_sequencia)
	into STRICT	vl_baixa_w,
		vl_pago_multa_w,
		vl_pago_juros_w,
		nr_sequencia_w
	from 	titulo_pagar_baixa
	where	nr_titulo = nr_titulo_p;

	if (coalesce(nr_sequencia_w,0) <> 0) then

		update	titulo_pagar_baixa
		set	vl_pago = ( coalesce(vl_baixa_w,0) + coalesce(vl_pago_multa_w,0) + coalesce(vl_pago_juros_w,0) ),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_titulo = nr_titulo_p
		and	nr_sequencia = nr_sequencia_w;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
