CREATE OR REPLACE PROCEDURE atualizar_volume_hemocomp ( nr_seq_derivado_p bigint, qt_vol_hemocomp_p INOUT bigint, qt_procedimento_p INOUT bigint, ie_opcao_p text) AS $body$
DECLARE

qt_volume_w		double precision := 0;
/* ie_opcao_p
B - Quantidade de bolsas solicitadas de acordo com volume informado.
V - Volume solicitado conforme quantidade de bolsas informada.
BV - quantidade 1 bolsa e volume de cada bolsa conforma cadastro no Shift + F11
*/
BEGIN

select	max(qt_volume)
into STRICT	qt_volume_w
from	san_derivado
where	nr_sequencia = nr_seq_derivado_p;

if (ie_opcao_p = 'B') and (coalesce(qt_vol_hemocomp_p,0) > 0) then
	begin
	if (qt_vol_hemocomp_p mod coalesce(qt_volume_w,qt_vol_hemocomp_p)  > 0) then
		qt_procedimento_p		:= trunc(dividir(qt_vol_hemocomp_p,qt_volume_w))+1;
	else
		qt_procedimento_p		:= (dividir(qt_vol_hemocomp_p,coalesce(qt_volume_w,qt_vol_hemocomp_p)));
	end if;
	end;
elsif	((ie_opcao_p = 'V') or (ie_opcao_p = 'BV')) and (qt_procedimento_p > 0) then
	begin
	qt_vol_hemocomp_p		:= (coalesce(qt_volume_w,qt_vol_hemocomp_p) * qt_procedimento_p);
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
