SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualizar_w_plan_teste_migr ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_aprovada_w	varchar(1);

BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	CASE WHEN coalesce(dt_aprovacao,'')='' THEN 'N'  ELSE 'S' END
	into STRICT	ie_aprovada_w
	from	w_plan_teste_migr
	where	nr_sequencia = nr_sequencia_p;

	if (ie_aprovada_w = 'N') then
		begin
		update	w_plan_teste_migr
		set	dt_aprovacao	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
		end;
	else
		begin
		update	w_plan_teste_migr
		set	dt_aprovacao	 = NULL,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
