SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_acomp_atend (nr_atendimento_p bigint, cd_convenio_p bigint) AS $body$
DECLARE

			    
cd_pessoa_fisica_p	varchar(10);
ie_permite_acomp_p	varchar(255);

BEGIN
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_p 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	select	obter_se_permite_acomp(cd_pessoa_fisica_p,coalesce(cd_convenio_p,0)) 
	into STRICT	ie_permite_acomp_p 
	;
 
	if (ie_permite_acomp_p <> 'XPTO') then 
		 
		update	atendimento_paciente 
		set	ie_permite_acomp = ie_permite_acomp_p 
		where	nr_atendimento = nr_atendimento_p;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
