CREATE OR REPLACE PROCEDURE atualiza_amostra_rack ( nr_seq_armazem_rack_p bigint, nr_seq_lab_am_rack_p bigint, nm_usuario_p text, ie_divide_amostra_p text, amostra_atualizada_p INOUT text ) AS $body$
DECLARE

    gravado_w   varchar(1);

BEGIN
    gravado_w := 'N';
    BEGIN
        IF ( ie_divide_amostra_p = 'N' )
        THEN
            UPDATE lab_soro_amostra_rack
            SET
                ds_amostra = ' ',
                ie_status = 'L'
            WHERE
                nr_sequencia = nr_seq_lab_am_rack_p;

        ELSE
            UPDATE lab_soro_amostra_rack
            SET
                ds_observacao = obter_desc_expressao(882962)
            WHERE
                nr_sequencia = nr_seq_lab_am_rack_p;

        END IF;

        INSERT INTO lab_soro_processo_info(
            nr_sequencia,
            ie_acao,
            dt_acao,
            nr_seq_armazena_rack,
            nr_seq_amostra_rack,
            dt_atualizacao,
            nm_usuario
        ) VALUES (
            nextval('lab_soro_processo_info_seq'),
            'I',
            clock_timestamp(),
            nr_seq_armazem_rack_p,
            nr_seq_lab_am_rack_p,
            clock_timestamp(),
            nm_usuario_p
        );

        IF ( ie_divide_amostra_p = 'N' )
        THEN
            DELETE FROM lab_soro_exame_amostra
            WHERE
                nr_seq_amostra_rack = nr_seq_lab_am_rack_p;

        END IF;
        gravado_w := 'S';
        COMMIT;
    EXCEPTION
        WHEN OTHERS THEN
            gravado_w := 'N';
    END;

    amostra_atualizada_p := gravado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
