DECLARE


cd_convenio_w	bigint;
nr_sequencia_w	bigint;

c01 CURSOR FOR
	SELECT	a.cd_convenio,
		c.nr_sequencia
	from	convenio a, convenio_estrutura_conta c, convenio_estrut_atrib b
	where	a.cd_convenio	= c.cd_convenio
	and	c.nr_sequencia	= b.nr_sequencia
	and	c.cd_estrutura	= 11
	and	not exists (SELECT 1 from convenio_estrut_atrib x
		where	x.nm_atributo 	= 'CD_CID_ATEND'
		and	x.nr_sequencia  = c.nr_sequencia)
	group by a.cd_convenio, c.nr_sequencia;


