		NM_ATRIBUTO                    ,
		NR_SEQ_ATRIBUTO                ,
		DT_ATUALIZACAO                 ,
		NM_USUARIO                     ,
		NM_ALIAS                       ,
		DS_MASCARA                     ,
		IE_TOTALIZA                    ,
		IE_QUEBRA                      ,
		QT_TAMANHO                     ,
		IE_IMPRIME                     ,
