		QT_TAM_FONTE                   ,
		NR_SEQ_INTERNO                 ,
		IE_ESTENDER		       ,
		IE_ORDENA_DECRESCENTE)
	values (nr_sequencia_w,
		'CD_CID_ATEND',
		99,
		clock_timestamp(),
		'tasy',
		null,
		null,
		null,
		null,
		8,
		null,
		null,
		null,
		null,
		null,
