CREATE OR REPLACE PROCEDURE atualiza_baixa_cartao (nr_seq_baixa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_conta_banco_w	bigint;
nr_seq_movto_w		bigint;


BEGIN

if (nr_seq_baixa_p IS NOT NULL AND nr_seq_baixa_p::text <> '') then
	begin
	
	select	max(nr_seq_conta_banco),
		max(nr_seq_movto)
	into STRICT	nr_seq_conta_banco_w,
		nr_seq_movto_w
	from	movto_cartao_cr_baixa
	where	nr_sequencia	= nr_seq_baixa_p;

	if (nr_seq_conta_banco_w IS NOT NULL AND nr_seq_conta_banco_w::text <> '') then
		CALL gerar_movto_baixa_cartao(nr_seq_baixa_p,nm_usuario_p,0);
	end if;

	CALL atualiza_repasse_cartao(nr_seq_baixa_p,nm_usuario_p);

	CALL atualizar_saldo_cartao_cr(nr_seq_movto_w,nm_usuario_p);
	exception when others then
		rollback;
		/*1700499 - Se ocorrer excessao que impeça movimentação bancaria, atualizacao do repasse ou saldo, deletar a baixa gerada para não deixar o processo pela metade.*/

		delete	FROM movto_cartao_cr_baixa	
		where	nr_sequencia	= nr_seq_baixa_p;
		
		commit;
		
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1030965, 'DS_ERRO_W=' || sqlerrm);
	end;
		
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
