CREATE OR REPLACE PROCEDURE atualiza_baixa_prescr_material (nr_prescricao_p bigint, nr_sequencia_p bigint, dt_baixa_p timestamp, cd_motivo_baixa_query_p bigint, cd_material_baixa_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (Verifica_saldo_baixar(nr_prescricao_p, nr_sequencia_p, cd_estabelecimento_p,
				cd_motivo_baixa_query_p, nm_usuario_p) = 'S') then

	update	prescr_material
	set	dt_baixa	  =	dt_baixa_p,
		cd_motivo_baixa   = 	cd_motivo_baixa_query_p,
		cd_material_baixa =	CASE WHEN coalesce(cd_material_baixa_p,0)=0 THEN  cd_material_baixa  ELSE cd_material_baixa_p END ,
		nm_usuario	  =	nm_usuario_p
	where	nr_prescricao	  = 	nr_prescricao_p
	and 	nr_sequencia   	  =	nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
