SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_base_calculo_trib_nf ( nr_sequencia_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_mercadoria_w			nota_fiscal.vl_mercadoria%type;
qt_existe_trib_w			integer;


BEGIN

if (nr_sequencia_nf_p IS NOT NULL AND nr_sequencia_nf_p::text <> '') then

	select	count(*)
	into STRICT	qt_existe_trib_w
	from	nota_fiscal_trib
	where	nr_sequencia	= nr_sequencia_nf_p;

	if (qt_existe_trib_w > 0) then

		select	max(vl_mercadoria)
		into STRICT	vl_mercadoria_w
		from	nota_fiscal
		where	nr_sequencia	 = nr_sequencia_nf_p;

		update	nota_fiscal_trib
		set	vl_base_calculo	= vl_mercadoria_w,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao  	= clock_timestamp()
		where	nr_sequencia	= nr_sequencia_nf_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
