CREATE OR REPLACE PROCEDURE atualiza_bipagem_bolsa (nr_seq_bolsa_p bigint, qt_dose_vol_p bigint, nr_atendimento_p bigint, dt_inicio_adm_p timestamp, dt_fim_adm_p timestamp, ds_observacao_p text, nm_usuario_p text, ie_acao_p text) AS $body$
DECLARE

 
nr_seq_interno_w	bigint;


BEGIN 
if (coalesce(nr_atendimento_p,0) > 0) then 
  select  max(nr_seq_interno) 
  into STRICT    nr_seq_interno_w 
  from   atend_paciente_unidade 
  where  nr_atendimento     = nr_atendimento_p 
  and    cd_setor_atendimento = obter_setor_atendimento(nr_atendimento_p);
end if;
 
update san_producao 
set qt_vol_transf     = qt_dose_vol_p, 
dt_utilizacao         = dt_inicio_adm_p, 
dt_termino_util       = dt_fim_adm_p, 
ds_observacao       = ds_observacao_p, 
qt_unidade_deriv_util  = CASE WHEN ie_acao_p='A' THEN  1  ELSE null END , 
nm_usuario_resp     = nm_usuario_p, 
nr_seq_atend_pac_unid = nr_seq_interno_w 
where nr_sequencia   = nr_seq_bolsa_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
