CREATE OR REPLACE PROCEDURE atualiza_bloqueio_acompanhante ( nr_atendimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*  -- ie_opcao_p --
B - Bloquear
D - Desfazer Bloqueio

BV - Block Visitor
UV - Undo Block Visitor

BC - Block Companion
UC - Undo Block Companion 

*/
						
						

BEGIN

If (ie_opcao_p = 'B') then
	update	atendimento_paciente
	set	ie_permite_acomp = 'N',
		ie_permite_visita = 'N',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_atendimento = nr_atendimento_p;
Elsif (ie_opcao_p = 'D') then
	update	atendimento_paciente
	set	ie_permite_acomp = 'S',
		ie_permite_visita = 'S',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_atendimento = nr_atendimento_p;
elsif (ie_opcao_p = 'BV') then
	update	atendimento_paciente
	set	ie_permite_visita = 'N',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_atendimento = nr_atendimento_p;
elsif (ie_opcao_p = 'UV') then
	update	atendimento_paciente
	set	ie_permite_visita = 'S',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_atendimento = nr_atendimento_p;
elsif (ie_opcao_p = 'BC') then
	update	atendimento_paciente
	set	ie_permite_acomp = 'N',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_atendimento = nr_atendimento_p;
elsif (ie_opcao_p = 'UC') then
	update	atendimento_paciente
	set	ie_permite_acomp = 'S',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_atendimento = nr_atendimento_p;
End if;

commit;

end;
