SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_brasindice_estrut (DT_INICIO_VIGENCIA_P timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_material_w		bigint;
ie_atualiza_estrutura_w	varchar(1);
nr_seq_mat_estrutura_w	bigint;
qt_reg_w		bigint;
nr_seq_mat_estr_vig_w	bigint;
dt_final_vigencia_w	timestamp;

c03 CURSOR FOR
	SELECT 	nr_sequencia
	from 	MAT_ESTRUTURA
	where	ie_situacao = 'A'
	and 	coalesce(ie_atualiza_brasindice,'N') = 'S'
	and 	coalesce(ie_atualiza_estrutura_w,'N') = 'S';

c04 CURSOR FOR
	SELECT 	cd_material,
		nr_sequencia
	from 	MAT_ESTRUTURA_CADASTRO
	where 	nr_seq_estrutura = nr_sequencia_w;


BEGIN

ie_atualiza_estrutura_w:=  coalesce(obter_valor_param_usuario(705, 27, obter_perfil_ativo, nm_usuario_p, 0), 'N');

open C03;
loop
fetch C03 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin

	open C04;
	loop
	fetch C04 into
		cd_material_w,
		nr_seq_mat_estrutura_w;
	EXIT WHEN NOT FOUND; /* apply on C04 */
		begin

		select 	count(*)
		into STRICT	qt_reg_w
		from 	W_BRASINDICE_ESTRUT_INT
		where 	nr_seq_estrutura = nr_seq_mat_estrutura_w
		and 	dt_vigencia = DT_INICIO_VIGENCIA_P
		and 	cd_material = cd_material_w
		and 	nm_usuario = nm_usuario_p;


		if (qt_reg_w = 0) then

			select 	coalesce(max(nr_sequencia),0)
			into STRICT	nr_seq_mat_estr_vig_w
			from 	mat_estrutura_cad_vig
			where 	nr_seq_mat_estrutura = nr_seq_mat_estrutura_w;

			if (nr_seq_mat_estr_vig_w = 0) then
				insert into mat_estrutura_cad_vig(
					NR_SEQUENCIA, NR_SEQ_MAT_ESTRUTURA, DT_ATUALIZACAO, NM_USUARIO, DT_ATUALIZACAO_NREC, NM_USUARIO_NREC, DT_INICIO_VIGENCIA, DT_FINAL_VIGENCIA)
				values (nextval('mat_estrutura_cad_vig_seq'), nr_seq_mat_estrutura_w , clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, to_date('01/01/2000','dd/mm/yyyy'), DT_INICIO_VIGENCIA_P);
			else
				select 	max(dt_final_vigencia)
				into STRICT	dt_final_vigencia_w
				from 	mat_estrutura_cad_vig
				where 	nr_sequencia = nr_seq_mat_estr_vig_w;

				if (coalesce(dt_final_vigencia_w::text, '') = '') then
					update 	mat_estrutura_cad_vig
					set 	DT_FINAL_VIGENCIA = DT_INICIO_VIGENCIA_P
					where 	nr_sequencia = nr_seq_mat_estr_vig_w;
				end if;

			end if;

		end if;

		end;
	end loop;
	close C04;

	end;
end loop;
close C03;

delete from W_BRASINDICE_ESTRUT_INT where nm_usuario = nm_usuario_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
