SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_calorias_npt (nr_sequencia_p bigint) AS $body$
DECLARE


qt_total_caloria_w			double precision;
nr_seq_elemento_w			bigint;
qt_kcal_elem_w				double precision;
pr_total_w					double precision :=0;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
		coalesce(qt_kcal,0)
from	nut_pac_elemento a
where	nr_seq_nut_pac	= nr_sequencia_p;


BEGIN

select	sum(qt_kcal)
into STRICT 	qt_total_caloria_w
from	nut_pac_elemento
where	nr_seq_nut_pac	= nr_sequencia_p;

OPEN c01;
LOOP
FETCH C01	into
		nr_seq_elemento_w,
      	qt_kcal_elem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	pr_total_w		:= dividir(qt_kcal_elem_w * 100, qt_total_caloria_w);

	update	nut_pac_elemento
	set		pr_total	= pr_total_w
	where	nr_sequencia	= nr_seq_elemento_w;

END LOOP;
CLOSE C01;

update	Nut_pac
set		qt_kcal_total		= qt_total_caloria_w
where	nr_sequencia		= nr_sequencia_p;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then
	commit;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
