CREATE OR REPLACE PROCEDURE atualiza_cat_conv_cirurgia ( nr_atendimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_w	integer;
cd_categoria_w	varchar(10);
nr_cirurgia_w	bigint;
nr_prescricao_w	bigint;

c01 CURSOR FOR
	SELECT	cd_convenio,
		cd_categoria,
		nr_cirurgia,
		nr_prescricao
	from	cirurgia
	where	nr_atendimento = nr_atendimento_p;


BEGIN

open c01;
loop
fetch c01 into
	cd_convenio_w,
	cd_categoria_w,
	nr_cirurgia_w,
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	update	cirurgia
	set	cd_convenio 	= cd_convenio_p,
		cd_categoria	= cd_categoria_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_cirurgia	= nr_cirurgia_w;

	update	prescr_material
	set	cd_convenio 	= cd_convenio_p,
		cd_categoria	= cd_categoria_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_prescricao	= nr_prescricao_w
	and	cd_convenio	= cd_convenio_w
	and	cd_categoria	= cd_categoria_w;

end loop;
close c01;

commit;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
