SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_cd_profissional_laudo ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, cd_profissional_laudo_p text, nm_usuario_p text) AS $body$
DECLARE


ie_existe_medico_w		varchar(1);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

   select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
   into STRICT		ie_existe_medico_w
   from		medico
   where	cd_pessoa_fisica = cd_profissional_laudo_p;

   if (ie_existe_medico_w <> 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(279533);
   end if;

   update	prescr_procedimento
   set	  	cd_profissional_laudo = cd_profissional_laudo_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao = clock_timestamp()
   where	nr_prescricao = nr_prescricao_p
   and		nr_sequencia  = nr_seq_prescr_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
