CREATE OR REPLACE PROCEDURE atualiza_cirurgia_superior ( nr_cirurgia_p bigint, nr_cirurgia_superior_p bigint) AS $body$
DECLARE

ie_estrutura_pepo_w	varchar(1);
nr_seq_pepo_w		bigint;

BEGIN

ie_estrutura_pepo_w := obter_param_usuario(872, 158, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_estrutura_pepo_w);

if (ie_estrutura_pepo_w = 'S') then
	select	max(nr_seq_pepo)
	into STRICT	nr_seq_pepo_w
	from	cirurgia
	where	nr_cirurgia = nr_cirurgia_p;

	update 	cirurgia
	set 	nr_cirurgia_superior = nr_cirurgia_superior_p
	where	nr_seq_pepo = nr_seq_pepo_w;
else
	update 	cirurgia
	set 	nr_cirurgia_superior = nr_cirurgia_superior_p
	where	nr_cirurgia = nr_cirurgia_p;
end if;

commit;

end;
$body$
