CREATE OR REPLACE PROCEDURE atualiza_classif_risco_triagem (nr_seq_triagem_p bigint, nr_seq_classif_p bigint, ds_justificativa_p text, nm_usuario_p text, nr_seq_triagem_prioridade_p bigint default null, cd_clinica_p bigint default null) AS $body$
DECLARE


nr_atendimento_w		bigint;
nr_seq_classif_w		bigint;
nr_seq_triagem_prioridade_w	bigint;


BEGIN

if (coalesce(nr_seq_triagem_p,0) > 0) then

	if (coalesce(nr_seq_classif_p,0) > 0) then

		update	triagem_pronto_atend
		set	nr_seq_classif 			= nr_seq_classif_p,
			ds_justificativa_classif 	= coalesce(ds_justificativa_p,ds_justificativa_classif),
			nm_usuario			= nm_usuario_p,
			dt_atualizacao  		= clock_timestamp()
		where	nr_sequencia 			= nr_seq_triagem_p;

	end if;

	if (coalesce(nr_seq_triagem_prioridade_p,0) > 0) then

		update	triagem_pronto_atend
		set	nm_usuario			= nm_usuario_p,
			dt_atualizacao  		= clock_timestamp(),
			nr_seq_triagem_prioridade	= nr_seq_triagem_prioridade_p
		where	nr_sequencia 			= nr_seq_triagem_p;

	end if;

	if (coalesce(cd_clinica_p,0) > 0) then

		update	triagem_pronto_atend
		set	nm_usuario			= nm_usuario_p,
			dt_atualizacao  		= clock_timestamp(),
			ie_clinica			= cd_clinica_p
		where	nr_sequencia 			= nr_seq_triagem_p;

	end if;

	select	coalesce(nr_atendimento,0),
		nr_seq_classif,
		nr_seq_triagem_prioridade
	into STRICT	nr_atendimento_w,
		nr_seq_classif_w,
		nr_seq_triagem_prioridade_w
	from	triagem_pronto_atend
	where	nr_sequencia = nr_seq_triagem_p;

	if (nr_atendimento_w > 0) then
		update	atendimento_paciente
		set 	nr_seq_triagem 			= nr_seq_classif_w,
			nr_seq_triagem_prioridade 	= nr_seq_triagem_prioridade_w
		where	nr_atendimento			= nr_atendimento_w;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
