SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_cliente_obs (nm_usuario_p text, nr_seq_cliente_p bigint, ds_observacao_p text, ie_tipo_p text) AS $body$
DECLARE


/*
ie_tipo_p
S - Adicionou restrição
N- Removeu restrição
*/
ds_obs_automatica_w	com_cliente_obs.ds_observacao%type;


BEGIN

ds_obs_automatica_w := wheb_mensagem_pck.get_texto(357927, 'nm_usuario=' || nm_usuario_p || ';dia=' || to_char(clock_timestamp(),'dd/MM/yyyy hh24:mi:ss'));

insert into com_cliente_obs(	nr_sequencia,
				nr_seq_cliente,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ds_observacao)
			values (	nextval('com_cliente_obs_seq'),
				nr_seq_cliente_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				CASE WHEN ie_tipo_p='S' THEN ds_observacao_p WHEN ie_tipo_p='N' THEN ds_obs_automatica_w END );

update	com_cliente a
set	a.ie_restricao		= ie_tipo_p
where 	a.nr_sequencia		= nr_seq_cliente_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
