CREATE OR REPLACE PROCEDURE atualiza_clinica_queixa (cd_estabelecimento_p bigint, nr_seq_queixa_p bigint, cd_pessoa_fisica_p text, ie_clinica_p INOUT bigint, cd_medico_p INOUT text) AS $body$
DECLARE



ie_clinica_w		integer;
qt_idade_pac_w		bigint;
dt_nascimento_w		timestamp;

C01 CURSOR FOR
	SELECT	ie_clinica
	from	queixa_paciente_clinica
	where	nr_seq_queixa	= nr_seq_queixa_p
	and	qt_idade_max	>= qt_idade_pac_w
	order by qt_idade_max desc;


BEGIN

select	coalesce(max(dt_nascimento),clock_timestamp())
into STRICT	dt_nascimento_w
from	pessoa_fisica
where	cd_pessoa_fisica	= cd_pessoa_fisica_p;

select	(obter_idade(dt_nascimento_w,clock_timestamp(),'A'))::numeric
into STRICT	qt_idade_pac_w
;


OPEN C01;
LOOP
FETCH C01 into
	ie_clinica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ie_clinica_w	:= ie_clinica_w;
	end;
END LOOP;
CLOSE C01;

ie_clinica_p	:= ie_clinica_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
