CREATE OR REPLACE PROCEDURE atualiza_cod_medico_crm ( nr_sequencia_p bigint, cod_crm_medico_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	select	obter_cod_medico_crm(cod_crm_medico_p)
	into STRICT	cd_pessoa_fisica_w
	;

	if (cd_pessoa_fisica_w <> '') then

		update	atendimento_visita
		set	cd_pessoa_fisica = cd_pessoa_fisica_w,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_sequencia_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
