CREATE OR REPLACE PROCEDURE atualiza_complemento_ditado ( nr_seq_interno_p bigint, nr_sequencia_p bigint, ds_arquivo_p text, nm_usuario_p text, dt_atualizacao_p INOUT timestamp) AS $body$
DECLARE


nr_sequencia_ditado_w 		bigint;
nr_prescricao_w       		bigint;
nr_sequencia_prescricao_w	bigint;
data_registro_w           timestamp;


BEGIN

data_registro_w := clock_timestamp();

select max(nr_sequencia)
into STRICT nr_sequencia_ditado_w
from prescr_proc_ditado
where NR_SEQ_PRESCR_PROC = nr_seq_interno_p;

if (ds_arquivo_p IS NOT NULL AND ds_arquivo_p::text <> '') then
	insert into prescr_proc_ditado_compl
	values (nr_sequencia_p, data_registro_w, nm_usuario_p, data_registro_w,  nm_usuario_p, nr_sequencia_ditado_w, ds_arquivo_p);
else
	update prescr_proc_ditado_compl
	set nm_usuario = nm_usuario_p, dt_atualizacao = data_registro_w
	where nr_seq_ditado = nr_sequencia_ditado_w
	and nr_sequencia = nr_sequencia_p;
end if;

select nr_prescricao, nr_sequencia
into STRICT nr_prescricao_w, nr_sequencia_prescricao_w
from prescr_procedimento
where nr_seq_interno = nr_seq_interno_p;

CALL gravar_auditoria_mmed(nr_prescricao_w, nr_sequencia_prescricao_w, nm_usuario_p, 34, null);

commit;

dt_atualizacao_p := data_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
