CREATE OR REPLACE PROCEDURE atualiza_comp_kit_est ( nr_seq_kit_estoque_p bigint, nr_sequencia_p bigint, qt_atendida_p bigint, nm_usuario_p text, ie_atualizar_util_p text default 'X') AS $body$
DECLARE


cd_material_w			integer;
nr_seq_item_kit_w			integer;
qt_material_w			double precision;
nr_sequencia_w			integer;
ie_atualizar_util_w 			varchar(1);


BEGIN

ie_atualizar_util_w := coalesce(ie_atualizar_util_p,'X');

if (coalesce(qt_atendida_p,0) > 0 and
	(nr_seq_kit_estoque_p IS NOT NULL AND nr_seq_kit_estoque_p::text <> '')) then
	begin

	if ie_atualizar_util_p <> 'N' then
		ie_atualizar_util_w := 'S';
	end if;

	select 	coalesce(max(cd_material),0),
		coalesce(max(nr_seq_item_kit),0),
		coalesce(max(qt_material),0)
	into STRICT	cd_material_w,
		nr_seq_item_kit_w,
		qt_material_w
	from	kit_estoque_comp
	where	nr_sequencia = nr_sequencia_p
	and	nr_seq_kit_estoque = nr_seq_kit_estoque_p;

	if (qt_material_w <> qt_atendida_p) then
		begin

		select	coalesce(max(nr_sequencia),0) +1
		into STRICT	nr_sequencia_w
		from	kit_estoque_comp
		where	nr_seq_kit_estoque = nr_seq_kit_estoque_p;

		if (cd_material_w < 0) then
			insert into kit_estoque_comp(
				nr_seq_kit_estoque,
				nr_sequencia,
				cd_material,
				dt_atualizacao,
				nm_usuario,
				qt_material,
				ie_gerado_barras,
				nr_seq_lote_fornec,
				nr_seq_item_kit)
			values (	nr_seq_kit_estoque_p,
				nr_sequencia_w,
				cd_material_w,
				clock_timestamp(),
				nm_usuario_p,
				qt_atendida_p,
				'N',
				null,
				nr_seq_item_kit_w);
		end if;

		update	kit_estoque_comp
		set	qt_material = (qt_material_w - qt_atendida_p)
		where	nr_sequencia = nr_sequencia_p
		and	nr_seq_kit_estoque = nr_seq_kit_estoque_p;

		end;

	else	if (qt_material_w = qt_atendida_p) then
			update	kit_estoque_comp
			set	ie_gerado_barras = 'N'
			where	nr_sequencia = nr_sequencia_p
			and	nr_seq_kit_estoque = nr_seq_kit_estoque_p;
		end if;
	end if;
	end;
end if;

if	ie_atualizar_util_w = 'S' then
	update	kit_estoque
	set	dt_utilizacao = clock_timestamp(),
		nm_usuario_util = nm_usuario_p
	where	nr_sequencia = nr_seq_kit_estoque_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
