CREATE OR REPLACE PROCEDURE atualiza_conta_cont_item_solic ( nr_cot_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_conta_contabil_w			varchar(20);
nr_solic_compra_w				bigint;
nr_item_solic_compra_w			integer;
cd_material_w				integer;
cd_estabelecimento_w			smallint;
cd_local_estoque_w			smallint;
cd_centro_custo_w				bigint;
dt_solicitacao_compra_w			timestamp;
ie_tipo_conta_w				integer	:= 2;
cd_material_ww				integer;

c01 CURSOR FOR
SELECT	a.nr_solic_compra,
	a.nr_item_solic_compra,
	a.cd_material
from	cot_compra_item a
where	a.nr_cot_compra = nr_cot_compra_p
and	(a.nr_solic_compra IS NOT NULL AND a.nr_solic_compra::text <> '')

union

select	b.nr_solic_compra,
	b.nr_item_solic_compra,
	a.cd_material
from	cot_compra_item a,
	cot_compra_solic_agrup b
where	a.nr_cot_compra		= b.nr_cot_compra
and	a.nr_item_cot_compra	= b.nr_item_cot_compra
and	a.nr_cot_compra		= nr_cot_compra_p
and	(b.nr_solic_compra IS NOT NULL AND b.nr_solic_compra::text <> '');


BEGIN

open C01;
loop
fetch C01 into
	nr_solic_compra_w,
	nr_item_solic_compra_w,
	cd_material_ww;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	cd_estabelecimento,
		cd_local_estoque,
		dt_solicitacao_compra
	into STRICT	cd_estabelecimento_w,
		cd_local_estoque_w,
		dt_solicitacao_compra_w
	from	solic_compra
	where	nr_solic_compra = nr_solic_compra_w;

	select	cd_material
	into STRICT	cd_material_w
	from	solic_compra_item
	where	nr_solic_compra = nr_solic_compra_w
	and	((nr_item_solic_compra = nr_item_solic_compra_w) or (cd_material = cd_material_ww));

	SELECT * FROM SELECT * FROM define_conta_contabil(
			2, cd_estabelecimento_w, null, null, null, null, cd_material_w, null, null, cd_local_estoque_w, cd_conta_contabil_w, cd_centro_custo_w, null, dt_solicitacao_compra_w) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w INTO cd_conta_contabil_w, cd_centro_custo_w;

	if (length(cd_conta_contabil_w) = 0) then
		begin

		ie_tipo_conta_w	:= 3;

		if (coalesce(cd_centro_custo_w::text, '') = '') then
			ie_tipo_conta_w	:= 2;
		end if;

		SELECT * FROM define_conta_material(
			cd_estabelecimento_w, cd_material_w, ie_tipo_conta_w, null, null, null, null, null, null, null, cd_local_estoque_w, Null, dt_solicitacao_compra_w, cd_conta_contabil_w, cd_centro_custo_w, null) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w;
		end;
	end if;

	update	solic_compra_item
	set	cd_conta_contabil		= cd_conta_contabil_w
	where	nr_solic_compra		= nr_solic_compra_w
	and	((nr_item_solic_compra = nr_item_solic_compra_w) or (cd_material = cd_material_ww));

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
