CREATE OR REPLACE PROCEDURE atualiza_conta_mes_status ( cd_convenio_p bigint, nr_interno_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_mesano_w		timestamp;


BEGIN 
 
if (cd_convenio_p > 0) and (nr_interno_conta_p > 0) then 
	 
	select	trunc(coalesce(dt_ref_valida, clock_timestamp()),'dd') 
	into STRICT	dt_mesano_w 
	from	convenio 
	where	cd_convenio = cd_convenio_p;
	 
	update	conta_paciente 
	set	dt_atualizacao = clock_timestamp(), 
		nm_usuario = nm_usuario_p, 
		dt_mesano_referencia = dt_mesano_w 
	where	nr_interno_conta = nr_interno_conta_p;
 
	commit;
	 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
