CREATE OR REPLACE PROCEDURE atualiza_conta_paciente ( dt_mesano_referencia_p timestamp, nr_interno_conta_p bigint, nr_seq_protocolo_p bigint, nr_protocolo_p text, nm_usuario_p text) AS $body$
DECLARE


qt_fatur_etapa_w	bigint;
nr_seq_protocolo_w	conta_paciente.nr_seq_protocolo%type;


BEGIN

select	nr_seq_protocolo
into STRICT	nr_seq_protocolo_w
from	conta_paciente
where	nr_interno_conta        = nr_interno_conta_p;

if (coalesce(dt_mesano_referencia_p::text, '') = '') then
	begin
	update 	conta_paciente
	set    	dt_atualizacao 		= clock_timestamp(), 
		nm_usuario		= nm_usuario_p, 
		nr_protocolo 		= nr_protocolo_p, 
		nr_seq_protocolo 	= nr_seq_protocolo_p 
	where  	nr_interno_conta 	= nr_interno_conta_p;
	end;
elsif (dt_mesano_referencia_p IS NOT NULL AND dt_mesano_referencia_p::text <> '') then
	begin
	update 	conta_paciente
	set    	dt_atualizacao 		= clock_timestamp(),
		nm_usuario 		= nm_usuario_p, 
		nr_protocolo		= nr_protocolo_p, 
		nr_seq_protocolo 	= nr_seq_protocolo_p,
		dt_mesano_referencia	= dt_mesano_referencia_p
	where  	nr_interno_conta 	= nr_interno_conta_p;
	end;	
end if;

if (coalesce(nr_seq_protocolo_w::text, '') = '' and (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '')) then
	select 	count(*)
	into STRICT	qt_fatur_etapa_w
	from 	fatur_etapa_alta
	where 	ie_evento = 'B';

	if (qt_fatur_etapa_w > 0) then
		begin

		CALL gerar_etapa_inserir_protocolo(nr_interno_conta_p, nm_usuario_p);

		end;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
