SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_contrato_lib_usuario () AS $body$
DECLARE



nr_seq_contrato_w	bigint;
nr_seq_contrato_atual_w	bigint;
cd_perfil_w		bigint;
cd_setor_w		bigint;
nm_usuario_lib_w	contrato_usuario_lib.nm_usuario_lib%type;
ie_permite_w		varchar(1);

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_seq_contrato_atual
from	contrato a
where	a.ie_classificacao in ('AD','ER')
and not exists (
	SELECT	1
	from	contrato_usuario_lib x
	where	x.nr_seq_contrato = a.nr_sequencia);


c02 CURSOR FOR
SELECT	cd_perfil,
	cd_setor,
	ie_permite,
	nm_usuario_lib
from	contrato_usuario_lib
where	nr_seq_contrato = nr_seq_contrato_atual_w
and	coalesce(ie_geracao,'S') = 'S';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_contrato_w,
	nr_seq_contrato_atual_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		cd_perfil_w,
		cd_setor_w,
		ie_permite_w,
		nm_usuario_lib_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		insert into contrato_usuario_lib(
			nr_sequencia,
			nr_seq_contrato,
			dt_atualizacao,
			nm_usuario,
			nm_usuario_lib,
			ie_aviso_vencimento,
			cd_perfil,
			cd_setor,
			ie_permite,
			ie_aviso_revisao,
			ie_geracao)
		values (	nextval('contrato_usuario_lib_seq'),
			nr_seq_contrato_w,
			clock_timestamp(),
			'TasyImp',
			nm_usuario_lib_w,
			'N',
			cd_perfil_w,
			cd_setor_w,
			ie_permite_w,
			'N',
			'U');

		end;
	end loop;
	close C02;


	end;
end loop;
close C01;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
