CREATE OR REPLACE PROCEDURE atualiza_contrato_sc (nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nr_contrato_p bigint, nr_seq_regra_contrato_p bigint) AS $body$
DECLARE


qt_w	bigint;


BEGIN
select	count(*)
into STRICT	qt_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p
and	nr_item_solic_compra = nr_item_solic_compra_p;

if (qt_w > 0) then
	select	count(*)
	into STRICT	qt_w
	from	contrato_regra_nf
	where	nr_seq_contrato = nr_contrato_p
	and	nr_sequencia = nr_seq_regra_contrato_p;

	if (qt_w > 0) then
		update	solic_compra_item
		set	nr_contrato = nr_contrato_p,
			nr_seq_regra_contrato = nr_seq_regra_contrato_p
		where	nr_solic_compra = nr_solic_compra_p
		and	nr_item_solic_compra = nr_item_solic_compra_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
