SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_convenio_agenda_serv (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

		 
nr_seq_agenda_w		agenda_consulta.nr_sequencia%type;
cd_convenio_w		atend_categoria_convenio.cd_convenio %type;
cd_categoria_w		atend_categoria_convenio.cd_categoria%type;
cd_plano_w		atend_categoria_convenio.cd_plano_convenio%type;

c01 CURSOR FOR 
		SELECT 	c.nr_sequencia 
		from	agenda_consulta c, 
			agenda a 
		where 	c.nr_atendimento = nr_atendimento_p 
		and	a.cd_agenda	 = c.cd_agenda 
		and	a.cd_tipo_agenda = 5 
		and	c.cd_convenio 	 <> cd_convenio_w;

BEGIN
 
--cd_convenio_w	:=	obter_convenio_atendimento(nr_atendimento_p); 
select 	max(cd_convenio), 
	max(cd_categoria), 
	max(cd_plano_convenio) 
into STRICT 	cd_convenio_w, 
	cd_categoria_w, 
	cd_plano_w 
from 	Atend_categoria_convenio a 
where 	a.nr_atendimento		= nr_atendimento_p 
and 	a.dt_inicio_vigencia = (SELECT max(dt_inicio_vigencia) 
				from Atend_categoria_convenio b 
				where nr_atendimento	= nr_atendimento_p);
 
 
open c01;
	loop 
	fetch c01 into 
		nr_seq_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
 
	update	agenda_consulta 
	set	cd_convenio = cd_convenio_w, 
		cd_categoria = cd_categoria_w, 
		cd_plano = cd_plano_w, 
		nm_usuario = nm_usuario_p 
	where	nr_sequencia = nr_seq_agenda_w;
	 
	end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
