CREATE OR REPLACE PROCEDURE atualiza_cotacao_moeda_nota ( nr_sequencia_nf_p bigint, cd_moeda_p bigint, vl_cotacao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (cd_moeda_p IS NOT NULL AND cd_moeda_p::text <> '') and (vl_cotacao_p IS NOT NULL AND vl_cotacao_p::text <> '') then
	update	nota_fiscal
	set	cd_moeda_estrangeira = cd_moeda_p,
		vl_conv_estrangeiro = vl_cotacao_p
	where	nr_sequencia = nr_sequencia_nf_p;
	commit;
end if;

end;
