CREATE OR REPLACE PROCEDURE atualiza_dados_envio_protocolo ( nr_seq_protocolo_p bigint, dt_vecimento_p timestamp, ie_atualiza_p text, nm_arquivo_p text, nm_usuario_p text) AS $body$
BEGIN
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then 
	if (ie_atualiza_p = '1') then 
		 
		update	protocolo_convenio 
		set	dt_envio = clock_timestamp(), 
			nm_usuario_envio = nm_usuario_p, 
			ds_arquivo_envio = nm_arquivo_p 
		where	nr_seq_protocolo = nr_seq_protocolo_p;
		 
	elsif (ie_atualiza_p = '2') then	 
		update	protocolo_convenio 
		set	dt_envio = clock_timestamp(), 
			dt_entrega_convenio = trunc(clock_timestamp()), 
			nm_usuario_envio = nm_usuario_p, 
			ds_arquivo_envio = nm_arquivo_p 
		where	nr_seq_protocolo = nr_seq_protocolo_p;	
 
	elsif (ie_atualiza_p = '3') then	 
		update	protocolo_convenio 
		set	dt_envio = clock_timestamp(), 
			dt_entrega_convenio = trunc(clock_timestamp()), 
			dt_vencimento = trunc(dt_vecimento_p), 
			nm_usuario_envio = nm_usuario_p, 
			ds_arquivo_envio = nm_arquivo_p 
		where	nr_seq_protocolo = nr_seq_protocolo_p;
		 
	end if;
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
