CREATE OR REPLACE PROCEDURE atualiza_dados_nfe_enviada (nr_seq_lote_p bigint, ds_motivo_p text, nr_protocolo_p bigint, dt_recebimento_p timestamp, cd_status_p text) AS $body$
DECLARE

				   
nr_seq_nota_w		bigint;				
nr_chave_acesso_w	varchar(255);
				
C01 CURSOR FOR 
	SELECT 	nr_sequencia, 
		nfe_obter_chave_acesso(nr_sequencia) 
	from	nota_fiscal 
	where	nr_seq_nf_lote_nfe = nr_seq_lote_p;


BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	nr_seq_nota_w, 
	nr_chave_acesso_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	update nota_fiscal set nr_danfe = nr_chave_acesso_w 
	where	nr_sequencia = nr_seq_nota_w;
end loop;
close c01;
 
update nota_fiscal_lote_nfe set dt_envio  = clock_timestamp(), 
				DT_RETORNO = dt_recebimento_p, 
				DS_MENSAGEM_RETORNO = cd_status_p || ' - ' ||ds_motivo_p, 
				nr_protocolo = nr_protocolo_p 
where	nr_sequencia = nr_seq_lote_p;				
				 
				 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
