SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_dados_nfse_ipm ( nm_usuario_p text, cd_verificacao_nfse_p text, nr_nfse_p text, cd_estabelecimento_p bigint, cd_serie_nf_p text, nm_arquivo_nfse_p text, ds_link_rps_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_link_nfse_w	varchar(255);


BEGIN

select	coalesce(max(nr_sequencia), 0)
into STRICT	nr_sequencia_w
from	nota_fiscal
where	cd_estabelecimento	= cd_estabelecimento_p
--and	cd_serie_nf		= cd_serie_nf_p
and	nm_arquivo_nfse		= nm_arquivo_nfse_p
and	coalesce(nr_nfe_imp, 0)	<> nr_nfse_p;


if (nr_sequencia_w > 0) then

	if (coalesce(ds_link_rps_p::text, '') = '') then
		ds_link_nfse_w := 'https://www.nfs-e.net/datacenter/include/nfw/nfw_imprime_notas.php?codauten=' || cd_verificacao_nfse_p;
	else
		ds_link_nfse_w := ds_link_rps_p;
	end if;


	update	nota_fiscal
	set	nr_nfe_imp 		= nr_nfse_p,
		cd_verificacao_nfse	= cd_verificacao_nfse_p,
		ds_link_rps		= ds_link_nfse_w
	where	nr_sequencia 	= nr_sequencia_w;

	update	nota_fiscal_item
	set	nr_nfe_imp 	= nr_nfse_p
	where	nr_sequencia 	= nr_sequencia_w;

	insert into nota_fiscal_hist(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_nota,
		cd_evento,
		ds_historico)
	values (	nextval('nota_fiscal_hist_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		'27',
		'NFS-e: ' || nr_nfse_p);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
