SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_dados_nfse_link ( nr_nfe_imp_p text, cd_verificacao_nfse_p text, dt_emissao_nfe_p text, cd_serie_nf_p text, nr_nota_fiscal_p text, cd_cgc_emitente_p text, ds_link_rps_p text) AS $body$
DECLARE


dt_emissao_nfe_w	timestamp;
ie_situacao_w		varchar(1);
cd_cgc_emitente_w 	nota_fiscal.cd_cgc_emitente%type;
ds_link_rps_w		nota_fiscal.ds_link_rps%type;


BEGIN

cd_cgc_emitente_w := elimina_caractere_especial(cd_cgc_emitente_p);

ds_link_rps_w :=  coalesce(ds_link_rps_p,'no_invoice_url');

select  max(ie_situacao)
into STRICT	ie_situacao_w
from 	nota_fiscal
where 	nr_nota_fiscal  = nr_nota_fiscal_p
and	cd_serie_nf	= cd_serie_nf_p
and	((cd_cgc_emitente	= cd_cgc_emitente_w) or (cd_cgc_emitente_w = 'X'))
and 	ie_tipo_nota in ('SE','SD','SF','ST')
and	ie_situacao in (1,8);

if (dt_emissao_nfe_p IS NOT NULL AND dt_emissao_nfe_p::text <> '') then
	begin
	dt_emissao_nfe_w := replace(dt_emissao_nfe_p, 'T', ' ');

	exception when others then
		begin
		dt_emissao_nfe_w		:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'yyyy-mm-dd hh24:mi:ss');
		exception when others then
			begin
			dt_emissao_nfe_w	:= to_date(replace(dt_emissao_nfe_p, 'T', ' '), 'dd-mm-yyyy hh24:mi:ss');
			exception when others then
			--Não foi possível converter a data de emissão enviada no arquivo:
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(186114,'DT_EMISSAO_NFE=' || dt_emissao_nfe_p);
			end;
		end;


	end;
end if;


if (ie_situacao_w <> '8') then
	update	nota_fiscal
	set 	nr_nfe_imp		=  nr_nfe_imp_p,
		cd_verificacao_nfse 	= cd_verificacao_nfse_p,
		dt_emissao_nfe		= dt_emissao_nfe_w,
		ds_link_rps		= ds_link_rps_w
	where	nr_nota_fiscal		= nr_nota_fiscal_p
	and	cd_serie_nf		= cd_serie_nf_p
	and	((cd_cgc_emitente	= cd_cgc_emitente_w) or (cd_cgc_emitente_w = 'X'))
	and	ie_tipo_nota in ('SE','SD','SF','ST')
	and	ie_situacao = 1;
	
	
else
	update	nota_fiscal
	set 	nr_nfe_imp		= nr_nfe_imp_p,
		cd_verificacao_nfse 	= cd_verificacao_nfse_p,
		dt_emissao_nfe		= dt_emissao_nfe_w,
		dt_atualizacao_estoque	= clock_timestamp(),
		ie_situacao		= '1',
		ds_link_rps		= ds_link_rps_w
	where	nr_nota_fiscal		= nr_nota_fiscal_p
	and	cd_serie_nf		= cd_serie_nf_p
	and	((cd_cgc_emitente	= cd_cgc_emitente_w) or (cd_cgc_emitente_w = 'X'))
	and	ie_tipo_nota in ('SE','SD','SF','ST')
	and	ie_situacao = 8;

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
