SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_dado_estat_checkup ( qt_dado_p bigint, ds_dado_p text, nr_seq_tipo_dado_p bigint, nr_atendimento_p bigint, nm_coluna_p text, cd_pessoa_fisica_p text, nm_usuario_p text, dt_coluna_p timestamp default null) AS $body$
DECLARE


dt_coluna_w	timestamp;
ie_result_padrao_w	varchar(1);
qt_result_padrao_w	bigint;
ds_resultado_w		varchar(2000);
ie_contador_w		integer;

  i RECORD;

BEGIN

ie_contador_w := 0;

if ((qt_dado_p IS NOT NULL AND qt_dado_p::text <> '') or (ds_dado_p IS NOT NULL AND ds_dado_p::text <> '')) and (nr_seq_tipo_dado_p IS NOT NULL AND nr_seq_tipo_dado_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin

	if (dt_coluna_p IS NOT NULL AND dt_coluna_p::text <> '') then		
		dt_coluna_w	:= trunc(dt_coluna_p,'dd');
	else
		dt_coluna_w	:= trunc(to_date(nm_coluna_p,'dd/mm/yyyy'),'dd');
	end if;

	select	count(*)
	into STRICT	qt_result_padrao_w
	from	checkup_resultado_padrao
	where	nr_seq_dado = nr_seq_tipo_dado_p;

	if (coalesce(ds_dado_p::text, '') = '') and (qt_dado_p IS NOT NULL AND qt_dado_p::text <> '') then
		begin
	
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_result_padrao_w
			from	checkup_resultado_padrao
			where	nr_seq_dado = nr_seq_tipo_dado_p
			and		upper(ds_resultado) = upper(qt_dado_p);
	
		if (ie_result_padrao_w = 'S') or (qt_result_padrao_w = 0) then
			
			update	checkup_dado_estat
			set	qt_dado = qt_dado_p,
				ds_dado  = NULL,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp(),
				cd_pessoa_fisica = cd_pessoa_fisica_p
			where	nr_seq_tipo_dado = nr_seq_tipo_dado_p
			and	trunc(dt_referencia,'dd') = dt_coluna_w
			and 	nr_atendimento = nr_atendimento_p;	
		
		end if;
	end;
	end if;
	
	if (ds_dado_p IS NOT NULL AND ds_dado_p::text <> '') and (coalesce(qt_dado_p::text, '') = '') then
		begin
		
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_result_padrao_w
		from	checkup_resultado_padrao
		where	nr_seq_dado = nr_seq_tipo_dado_p
		and		upper(ds_resultado) = upper(ds_dado_p);
	
		if (ie_result_padrao_w = 'S') or (qt_result_padrao_w = 0) then
			
			update	checkup_dado_estat
			set	qt_dado  = NULL,
				ds_dado = ds_dado_p,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp(),
				cd_pessoa_fisica = cd_pessoa_fisica_p
			where	nr_seq_tipo_dado = nr_seq_tipo_dado_p
			and	trunc(dt_referencia,'dd') = dt_coluna_w
			and 	nr_atendimento = nr_atendimento_p;
		
		end if;
	end;
	end if;
	
	if (ie_result_padrao_w = 'N') and (qt_result_padrao_w > 0) then
		begin
				
		for i in (SELECT ds_resultado
			from	checkup_resultado_padrao
			where	nr_seq_dado = nr_seq_tipo_dado_p) loop

		ds_resultado_w := ds_resultado_w || i.ds_resultado;
		ie_contador_w := ie_contador_w + 1;

			if (ie_contador_w < qt_result_padrao_w) then
				ds_resultado_w := ds_resultado_w || ', ';
			else
				ds_resultado_w := ds_resultado_w || '.';
			end if;
		end loop;
	
		-- "Resultado informado invalido. Resultado(s) valido(s): "
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1171647, 'DS_RESULTADO=' || ds_resultado_w);
		end;
	end if;	
	
	CALL calcular_valor_dado_estat(nr_atendimento_p);
	end;
	
end if;	
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualiza_dado_estat_checkup ( qt_dado_p bigint, ds_dado_p text, nr_seq_tipo_dado_p bigint, nr_atendimento_p bigint, nm_coluna_p text, cd_pessoa_fisica_p text, nm_usuario_p text, dt_coluna_p timestamp default null) FROM PUBLIC;

