CREATE OR REPLACE PROCEDURE atualiza_data_campo_pac_atend ( ds_campo_p text, nr_seq_atendimento_p bigint, ie_opcao_p text default 'A', nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


/*	ie_opcao_p
	A - atualizar
	D - desfazer */
ds_comando_w	varchar(2000);
ds_parametros_w	varchar(2000);
ds_sep_bv_w	varchar(10);
nm_usuario_w varchar(255);


BEGIN

if (ds_campo_p = 'DT_QUIMIO_LIBERADA') then
	nm_usuario_w := '	nm_usuario_lib	= :nm_usuario ';
else
	nm_usuario_w := '	nm_usuario	= :nm_usuario ';
end if;

if (coalesce(ie_opcao_p,'A') = 'A') then
	begin
	ds_comando_w	:=
		' update	paciente_atendimento ' ||
		' set	'||coalesce(ds_campo_p,'DT_RETIRADA_MEDICACAO')||'	= sysdate, ' ||
		nm_usuario_w ||
		' where	nr_seq_atendimento	= :nr_seq_atendimento ';
	end;
elsif (ie_opcao_p = 'D') then
	begin
	ds_comando_w	:=
		' update	paciente_atendimento ' ||
		' set	'||coalesce(ds_campo_p,'DT_RETIRADA_MEDICACAO')||'	= null, ' ||
    '	NM_USUARIO_LIB	= null, ' ||
    '	dt_liberacao	= null, ' ||
		'	nm_usuario	= :nm_usuario ' ||
		' where	nr_seq_atendimento	= :nr_seq_atendimento ';
	end;
end if;

ds_sep_bv_w	:= obter_separador_bv;
ds_parametros_w	:=
	'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
	'nr_seq_atendimento=' || nr_seq_atendimento_p || ds_sep_bv_w;
	
CALL exec_sql_dinamico_bv('', ds_comando_w, ds_parametros_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
