CREATE OR REPLACE PROCEDURE atualiza_data_campo_pac_med_js ( ds_campo_p text, nr_seq_atendimento_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*	ie_opcao_p
	A - atualizar
	D - desfazer */
ds_comando_w	varchar(2000);
ds_parametros_w	varchar(2000);
ds_sep_bv_w	varchar(10);


BEGIN

if (ie_opcao_p = 'A') then
	begin
	ds_comando_w	:=
		' update	paciente_atendimento ' ||
		' set	'||ds_campo_p||'	= sysdate, ' ||
		'	nm_retirada_pre_medicacao	= :nm_usuario ' ||
		' where	nr_seq_atendimento	= :nr_seq_atendimento ';
	end;
elsif (ie_opcao_p = 'D') then
	begin
	ds_comando_w	:=
		' update	paciente_atendimento ' ||
		' set	'||ds_campo_p||'	= null, ' ||
		'	nm_retirada_pre_medicacao	= null ' ||
		' where	nr_seq_atendimento	= :nr_seq_atendimento ';
	end;
end if;

ds_sep_bv_w	:= obter_separador_bv;

if (ie_opcao_p = 'A') then
	begin
	ds_parametros_w	:=
		'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
		'nr_seq_atendimento=' || nr_seq_atendimento_p || ds_sep_bv_w;
	end;
elsif (ie_opcao_p = 'D') then
	begin
	ds_parametros_w	:=
		'nr_seq_atendimento=' || nr_seq_atendimento_p || ds_sep_bv_w;
	end;
end if;
CALL exec_sql_dinamico_bv('', ds_comando_w, ds_parametros_w);

commit;

end;
$body$
