CREATE OR REPLACE PROCEDURE atualiza_data_emissao_proc_pac ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_imprime_auto_p text, nm_usuario_p text) AS $body$
DECLARE


ds_erro_w	varchar(4000);


BEGIN

if (ie_imprime_auto_p = 'I') then
	begin
	update	prescr_procedimento b
	set	b.dt_emissao_setor_atend = clock_timestamp()
	where	b.nr_prescricao	= nr_prescricao_p
	and	b.nr_sequencia	= nr_sequencia_p
	and	coalesce(b.dt_emissao_setor_atend::text, '') = ''
	and exists (	SELECT	1
			from	usuario_setor_v a
			where	a.cd_setor_atendimento = coalesce(b.cd_setor_atendimento, a.cd_setor_atendimento)
			and	a.nm_usuario = nm_usuario_p);
	end;
else
	begin
	update	prescr_procedimento b
	set	b.dt_emissao_setor_atend = clock_timestamp()
	where	b.nr_prescricao	= nr_prescricao_p
	and	coalesce(b.dt_emissao_setor_atend::text, '') = ''
	and exists (	SELECT	1
			from	usuario_setor_v a
			where	a.cd_setor_atendimento = coalesce(b.cd_setor_atendimento, a.cd_setor_atendimento)
			and	a.nm_usuario = nm_usuario_p);
	end;
end if;

commit;
exception when others then
	ds_erro_w	:= sqlerrm;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
