CREATE OR REPLACE PROCEDURE atualiza_data_previsao_alta ( nr_seq_agenda_p bigint) AS $body$
DECLARE

 
qt_tempo_w		bigint;					
nr_atendimento_w	bigint;
					

BEGIN 
 
select	max(obter_tempo_internacao(cd_medico,nr_seq_proc_interno)), 
	max(nr_atendimento) 
into STRICT	qt_tempo_w, 
	nr_atendimento_w 
from	agenda_paciente 
where	nr_sequencia = nr_seq_agenda_p;
 
if (coalesce(qt_tempo_w,0) > 0) and (coalesce(nr_atendimento_w,0) > 0) then 
	update	atendimento_paciente 
	set	dt_previsto_alta = (dt_entrada + qt_tempo_w/24) 
	where	nr_atendimento = nr_atendimento_w;
	commit;
end if;
	 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
