CREATE OR REPLACE PROCEDURE atualiza_data_prev_agenda_cir ( nr_seq_agenda_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

					 
dt_chegada_prev_w	timestamp;					
					 

BEGIN 
begin 
 
if (coalesce(nr_seq_agenda_p,0) > 0) or (coalesce(nr_atendimento_p,0) > 0) then 
	select	max(dt_chegada_prev) 
	into STRICT	dt_chegada_prev_w 
	from	agenda_paciente 
	where	nr_sequencia = nr_seq_agenda_p 
	or	nr_atendimento	= nr_atendimento_p;
 
	update	gestao_vaga 
	set	dt_prevista 	= dt_chegada_prev_w 
	where	nr_seq_agenda 	= nr_seq_agenda_p 
	or	nr_atendimento	= nr_atendimento_p;
 
	update	paciente_espera 
	set	dt_prevista_internacao 	= dt_chegada_prev_w 
	where	nr_seq_agenda 		= nr_seq_agenda_p 
	or	nr_atendimento		= nr_atendimento_p;
 
	commit;
end if;	
 
exception 
	when others then 
	dt_chegada_prev_w := null;
end;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
